mapper.select:
SELECT * FROM farmer_certificate WHERE 1=1

AND farmer_id=#{farmerId}

order by apply_date DESC

SELECT * FROM smart_Farm_certificate WHERE 1=1

AND id=#{id}

order by apply_date DESC

select a.ROWNUM, a.no,a.id, DATE_FORMAT(a.apply_date, '%Y-%m-%d') apply_date, DATE_FORMAT(a.start_date,'%Y-%m-%d') start_date,DATE_FORMAT(a.due_date,'%Y-%m-%d') due_date,DATE_FORMAT(approve_date,'%y-%m-%d') approve_date from (SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, f.* from (select * FROM smart_farm_certificate ORDER BY apply_date) f, (SELECT @ROWNUM:=0) TMP) a WHERE 1=1
AND id=#{applicate.id}
AND approve_date is null
AND a.ROWNUM BETWEEN ${paging.start} AND ${paging.end}

select count(*) count FROM smart_farm_certificate a,member m WHERE 1=1 and a.id=m.id
AND a.id=#{id}
AND approve_date is null
