mapper.select:
	select * 
	from (	select  @ROWNUM := @ROWNUM + 1 AS ROWNUM, f.plant_id , f.plant_cate,f.plant_titles 
  				from (	select plant_id, plant_cate, plant_titles 
              			from plant ,(SELECT @ROWNUM := 0 ) TMP  WHERE plant_titles LIKE CONCAT('%', #{searchword}, '%') 
              			) as f 
			where 1=1  AND plant_cate=#{plantcate}) as s where ROWNUM between ${start} and ${end}


select plant_info    
from plant      
where plant_id=${plantid}
